css:
@media screen {
    .print-format {
        max-height: 9.2cm;
        width: 20.1cm;
        max-width: unset !important;
        min-height: unset !important;
    }
}

.main {
    height: 9.2cm;
    width: 20.1cm;
}

.print-format {
    margin: 0mm !important;
    padding: 0mm !important;
    font-size: 10pt;
    font-family: Monospace, sans-serif;
}

.crossing {
    position: fixed;
    top: 1cm;
    border-top: 1px solid black;
    border-bottom: 1px solid black;
    height: 2em;
    transform: rotate(-45deg);
    width: 4.5cm;
    left: -1cm;
    display: flex;
    align-items: center;
    justify-content: center;
    transform-origin: 50% 50%;
}

.date {
    display: flex;
    position: fixed;
    top: var(--date-from-top);
    left: var(--date-from-left);
    line-height: 0.5cm;
}

.date > div {
    width: 0.5cm;
    height: 0.5cm;
}

.pay {
	position: fixed;
	top: var(--pay-from-top);
	left: var(--pay-from-left);
	line-height: 0.9cm;
}

.rupees_in_text {
    position: fixed;
    width: calc(var(--rupees-in-text-to-right-line1) - var(--rupees-in-text-from-left-line1));;
    top: var(--rupees-in-text-from-top-line1);
    left: var(--rupees-in-text-from-left-line2);
    line-height: 0.8cm;
}

.rupees_in_text:before {
  content: '';
  float: left;
  width: calc(var(--rupees-in-text-from-left-line1) - var(--rupees-in-text-from-left-line2));
  height: 1em;
}

.rupees {
    position: fixed;
    top: var(--rupees-from-top);
    left: var(--rupees-from-left);
    line-height: 0.9cm;
}

.aadhaar {
    display: flex;
    position: fixed;
    top: var(--aadhaar-from-top);
    left: var(--aadhaar-from-left);
    line-height: 0.5cm;
}

.aadhaar > div {
    width: 0.5cm;
    height: 0.5cm;
}

html:
{% set size = frappe.get_doc("Cheque Print Settings", "Cheque Print Settings") %}

<style>
    :root {
        --date-from-left: {{ (size.date_from_left or 0) }}cm;
        --date-from-top: {{ (size.date_from_top or 0) }}cm;

        --pay-from-left: {{ (size.pay_from_left or 0) }}cm;
        --pay-from-top: {{ (size.pay_from_top or 0) }}cm;

        --rupees-in-text-from-left-line1: {{ (size.rupees_in_text_from_left_line1 or 0) }}cm;
        --rupees-in-text-from-top-line1: {{ (size.rupees_in_text_from_top_line1 or 0) }}cm;
        --rupees-in-text-to-right-line1: {{ (size.rupees_in_text_to_right_line1 or 0) }}cm;

        --rupees-in-text-from-left-line2: {{ (size.rupees_in_text_from_left_line2 or 0) }}cm;
        --rupees-in-text-from-top-line2: {{ (size.rupees_in_text_from_top_line2 or 0) }}cm;

        --rupees-from-left: {{ (size.rupees_from_left or 0) }}cm;
        --rupees-from-top: {{ (size.rupees_from_top or 0) }}cm;

        --aadhaar-from-left: {{ (size.aadhaar_from_left or 0) }}cm;
        --aadhaar-from-top: {{ (size.aadhaar_from_top or 0) }}cm;
    }
</style>

<div class="main">
    {% set date = doc.get_formatted('date').replace('-', '') %}
    <div class="date">
        {% for d in date %}
            <div>{{ d }}</div>
        {% endfor %}
    </div>
    {% if (doc.crossing != "No Crossing") %}
        <div class="crossing">
            {% if (doc.crossing == "Cross A/C Payee") %}
                A/C PAYEE ONLY
            {% endif %}
        </div>
    {% endif %}
    <div class="pay">{{ doc.pay }}</div>
    <div class="rupees_in_text">{{ doc.rupees_in_text }}</div>
    <div class="rupees">{{ doc.get_formatted("rupees").replace('₹ ', '') }}</div>
    <div class="aadhaar">
        {% for a in (doc.aadhaar_no or '') %}
            <div>{{ a }}</div>
        {% endfor %}
    </div>
</div>
