                    {% set order_no = [] %}
                    {% if doc.type=="Compound Wall" %}
                        {% for i in doc.item_details_compound_wall %}
                            {% if (i.sales_order and i.sales_order not in order_no) %}
                                {% if (order_no|len > 0) %}
                                    {{", "}}
                                {% endif %}
                                <nobr>{{i.sales_order}} / {{ frappe.utils.formatdate(frappe.db.get_value("Sales Order", i.sales_order, "transaction_date")) }}</nobr>
                                {% set _=order_no.append(i.sales_order) %}
                            {% endif %}
                        {% endfor %}
                    {% endif %}
                     {% set order_no = [] %}
                    {% if doc.type=="Pavers" %}
                        {% for i in doc.item_details %}
                            {% if (i.sales_order and i.sales_order not in order_no) %}
                                {% if (order_no|len > 0) %}
                                    {{", "}}
                                {% endif %}
                               <nobr>{{i.sales_order}} / {{ frappe.utils.formatdate(frappe.db.get_value("Sales Order", i.sales_order, "transaction_date")) }}</nobr>
                                {% set _=order_no.append(i.sales_order) %}
                            {% endif %}
                        {% endfor %}
                    {% endif %}
                </td>
           </tr>
           <tr>
             <td style="width:50%"><b>RATE:{% if doc.type=="Compound Wall" %}
              {% set a=[] %}
                            {% for i in doc.item_details_compound_wall %} 
                           {% set _=a.append(i.rate or 0) %} {% endfor %}
                           {% if (a|length)>0 %}
                            {{"%.2f"|format((a|sum) /(a|length))}}
                            {% endif %}
                            {% endif %}
                             {% if doc.type=="Pavers" %}
                             {% set a=[] %}
                            {% for i in doc.item_details %}{% set _=a.append(i.rate or 0) %} {% endfor %}
                            {% if (a|length)>0 %}
                                {{"%.2f"|format((a|sum) /(a|length))}} 
