  </tr>
  <tr>
    <td><b>LAYER PAYMENT IN SQFT</b></td>
    <td>{{ (doc.total_layed_sqft or 0) | int}}  SQFT</td>
  </tr>
    <tr>
      <td><b>SCRAP RETURN</b></td>
      <td>{{ doc.returned_scrap_qty }} SQFT</td>
  </tr>
  <tr>
    <td><b style="font-size: 120%">SITE STOCK</b></td>
    <td>
        <b style="font-size: 120%">{{ doc.site_stock or 'NIL' }}</b>
    </td>
  </tr>
  </table>

    {% if doc.material_supply %}
        <b><h3>Materials Supplied</h3></b>
        <table style="width: 100%">
            <tr>
                <th>
                    Item
                </th>
                 <th>
                    Qty
                </th>
            </tr>
            {% set delivered_details=site_completion_delivery_uom(doc.name) %}
            {% for row in delivered_details %}
                <tr>
                    <td>
                        {{ row }}
                    </td>
                    <td>
