                            {% set _= comma.append(1) %}
                        {% endfor %}

                    </td>
                </tr>
            {% endfor %}
        </table>
    {% endif %}

    {% if other_work|len > 0 %}
        <b><h3>Other Work Description</h3></b>
        <table style="width: 100%">
            <tr>
                <th>
                    Job Worker
                </th>
                <th>
                    Start Date
                </th>
                <th>
                    Description
                </th>
                <th>
                    Sqft/Person
                </th>
                <th>
                    Rate
                </th>
                <th>
                    Amount
                </th>
            </tr>
            {% for row in doc.job_worker %}
                <tr>
                    {% if row.other_work %}
                        <td>
                            {{ row.employee_name }}
                        </td>
                        <td>
                            {{ frappe.utils.formatdate(row.start_date) }}
                        </td>
                        <td>
                            {{ row.description_for_other_work or "-" }}
                        </td>
                        <td>
                            {% if row.amount_calc_by_person %}
                                {{ row.no_of_person or 0 }} Person
