css:
.table, th, td {
  border: 1px solid black;
}
.bg_color {
    background-color : #e6e6e6;
}
td {
    font-weight: bold;
}
.td {
  padding: 5px;
  border: 1px solid black;
  text-align: left;    
}

.signature {
    width: 100%;
    margin-top: 10px;
}
.print-format td, .print-format th {
    vertical-align: top !important;
    padding: 4px !important;
}
.signature td {
    text-align: center;
    font-weight: bold;
    border-top: 1px solid black;
    padding-top: 50px !important;
}
.ts-amount {
    font-weight: bold;
    text-align: right;
}
.ts-total-row {
    font-size: 125%;
}

