                <tr>
                    <td>Actual Other Work</td>
                    <td class="ts-amount">{{ "%.2f"%d.unrelated_other_sqft }}</td>
                    <td class="ts-amount">{{ "%.2f"%d.unrelated_other_cost }}</td>
                    <td class="ts-amount">{{ "%.2f"%d.unrelated_other_per_sqft }}</td>
                </tr>
            </table>
        {% endif %}
<div><br></div>

{% set item_wise_so_rate = get_item_wise_so_rate(doc.name) %}
{% set item_wise_completion_rate = get_item_wise_completion_rate(doc.name) %}

<b><h3 style="margin-top:0">Actual Site Cost Split-Up</h3></b>
    {% for actual_item_cd in d.item_cost %}
         <b><h3 style="margin-top:10px">{{ actual_item_cd.item }}</h3></b>
        <table style="width: 100%">
            <tr>
                <th>
                    Expense
                </th>
                 <th>
                    Amount
                </th>
                <th>
                    Rate Per Supply Sqft
                </th>

            </tr>
            <tr>
                    <td>

                        Item Cost
                    </td>
                    <td class="ts-amount">

                         {{"%.2f"|format(actual_item_cd.amount)}}
                    </td>
                    <td class="ts-amount">
                        {{"%.2f"|format(actual_item_cd.rate) }}
                    </td>

                </tr>
                 <tr>
                    <td>

                        Raw Material Cost
                    </td>
                    <td class="ts-amount">
                       {{ total_raw_material|sum }}
                    </td>
                    <td class="ts-amount">
                        {{"%.2f"|format(raw_material_supplied_per_sqft)}}
                    </td>

                </tr>

                <tr>
                    <td>
                       Transport Cost
                    </td>
                    <td class="ts-amount">
                       {{"%.2f"|format((d.rental or 0)+(d.own_vehicle or 0)) }}
                    </td>
                        {% set ownvehicle_sqft = d.own_vehicle / (delivery_detail.get("own_vehicle") or 1)  %}
                        {% set rent_sqft = d.rental / (delivery_detail.get("rental_vehicle") or 1) %}
                    <td class="ts-amount">

                        {% set rent_and_own_vehicle_cost = ((d.rental or 0)+(d.own_vehicle or 0)) / (( (delivery_detail.get("own_vehicle") or 0) + (delivery_detail.get("rental_vehicle") or 0)  ) or 1) %}

                       {{"%.2f"|format( rent_and_own_vehicle_cost) }}
                    </td>
                </tr>

                <tr>
                    <td>
                      Job Work Rate
                    </td>
                    <td class="ts-amount">
                      {{"%.2f"|format(((d.job_work_rate.get(actual_item_cd.item) or {}).get('amount') or 0))}}

                    </td>
                     {% set job_work_sqft = ((d.job_work_rate.get(actual_item_cd.item) or {}).get('rate') or 0) %}
                    <td class="ts-amount">
                      {{"%.2f"|format(job_work_sqft)}}

                    </td>
                    </tr>
                    <tr>
                    <td>
                      Other Work Rate
                    </td>
                    <td class="ts-amount">
                      {{"%.2f"|format(d.other_rate) }}

                    </td>
                    {% set other_rate_sqft = d.other_rate / (a|sum or 1)  %}
                    <td class="ts-amount">
                      {{"%.2f"|format(other_rate_sqft)}}

                    </td>
                    </tr>
                    <tr>
                      <td>
                        Additional Cost
                    </td>
                    <td class="ts-amount">
                       {{"%.2f"|format(d.additional_cost)}}
                    </td>
                    {% set add_cost_sqft = d.additional_cost / (a|sum or 1)   %}
                    <td class="ts-amount">
                       {{"%.2f"|format(add_cost_sqft)}}
                    </td>
                </tr>

                <tr class='ts-total-row'>
                    <td > 
                        Total
                    </td>
                    {% set tot_row = d.rental + d.own_vehicle + ((d.job_work_rate.get(actual_item_cd.item) or {}).get('amount') or 0) + d.other_rate + d.additional_cost + actual_item_cd.amount + total_raw_material|sum %}
                    <td class="ts-amount">
                        {{"%.2f"|format(tot_row)}}
                    </td>
                    {% set tot_sqft = rent_and_own_vehicle_cost + ((d.other_rate + d.additional_cost + total_raw_material|sum) / (a|sum or 1))+ (actual_item_cd.rate or 0) + ((d.job_work_rate.get(actual_item_cd.item) or {}).get('rate') or 0) %}
                    <td class="ts-amount" > 
                        {{"%.2f"|format(tot_sqft)}}
                    </td>
                </tr>
                <tr>
                    <td>
                        Margin (SO rate: {{ "%.2f"%(item_wise_so_rate.get(actual_item_cd.item) or 0) }})
                    </td>
                    <td class="ts-amount">
                        {{ "%.2f"%( (item_wise_so_rate.get(actual_item_cd.item) or 0) - (tot_sqft or 0) ) }}
                    </td>
                    <td class="ts-amount">
                        {{ "%.2f"%( ((item_wise_so_rate.get(actual_item_cd.item) or 0) - (tot_sqft or 0))/(tot_sqft or 1)*100 ) }}%
                    </td>
                </tr>
                <tr>
                    <td>
                        Margin (Completion rate: {{ "%.2f"%(item_wise_completion_rate.get(actual_item_cd.item) or 0) }})
                    </td>
                    <td class="ts-amount">
                        {{ "%.2f"%( (item_wise_completion_rate.get(actual_item_cd.item) or 0) - (tot_sqft or 0) ) }}
                    </td>
                    <td class="ts-amount">
                        {{ "%.2f"%( ((item_wise_completion_rate.get(actual_item_cd.item) or 0) - (tot_sqft or 0))/(tot_sqft or 1)*100 ) }}%
                    </td>
                </tr>
               </table>
        {% endfor %}
        <!--{{ item_wise_so_rate }}-->
</body>
</html>
