css:
.main {
    width: 100%;
    font-size: 16px;
}
table {
    width: 100%;
}

table, tr, td, th {
    border: 1px solid black;
}

.header tr > td {
    width: 50%;
    text-align: left;
}

.header tr > td:last-child {
    text-align: right;
}

.ts-amount {
    text-align: right;
}
.ts-total-row {
    font-weight: bold;
}

h1, .h1, h2, .h2, h3, .h3 {
    margin-top: 10px !important;
    margin-bottom: 5px !important;
}

.hide {
    display: none;
}
