css:
.main{
    font-size:10px;
     font-family: Arial, Helvetica, sans-serif;
}
.head{
    text-align:center;
}
.bold{
    font-weight:bold;
}
td,th{
    font-size:80%;
    border:1px solid black;
}

html:
{% set dn_items = group_dn_items(doc.name) %}


{% if "doc.docstatus==1" %}
<div class="main">
    <div  class="head bold" style="font-size: 17px;">Sri Ganapathy Fly Ash Bricks</div><div class="head" style="font-size: 16px;">Palladam</div><div class="head bold" style="font-size: 17px;">------------------------------<br>DELIVERY SLIP<br>{{ {0:"DRAFT",1:"SUBMITTED",2:"CANCELLED"}.get(doc.docstatus) }}</div>
    <p style="line-height: 1.6;">
       <sapn class="bold"> Req No:</sapn> {{doc.name or " "}} <br>
       <sapn class="bold"> Date:</sapn> {{doc.get_formatted("posting_date")}} {{doc.get_formatted("posting_time")}}  <br>
       <sapn class="bold"> Customer:</sapn> {{doc.site_work}}  <br>
       <sapn class="bold"> Vehicle No:</sapn> {{ doc.own_vehicle_no or doc.vehicle_no or " "}} <br>
       <sapn class="bold"> Total KM:</sapn> {{doc.distance or " "}}  <br>
       <sapn class="bold"> Driver Name:</sapn> {{doc.driver_name_2 or doc.driver_name_1 or " "}}  <br>
       <sapn class="bold"> Operator:</sapn> {% if doc.transporter=="Own Transporter" %}{% if doc.operator_ %}{{doc.operator_}}{% endif %} {% else %}{% if doc.operator %}{{frappe.db.get_value("Employee", doc.operator, "employee_name")}}{% endif %}{%endif%} <br>
       <sapn class="bold"> Supervisor:</sapn> {{ doc.supervisor_name or " " }} <br>
       <sapn class="bold"> Phone:</sapn> {{doc.supervisor_phone_no or ""}} <br><br>
    </p>
    {% set only_rm = check_only_rm(doc.name) %}
    <table style="width:100%;"> 
        <tr>
            <th style="width:40%;">
                Item
            </th>
            <th>
                {% if not only_rm%}
                Bundle
                {% else %}
                Unit
                {% endif %}
            </th>
            {% if doc.value_pieces==1 and not only_rm %}
            <th>
                Pieces
            </th>
            {% endif %}
            {% if not only_rm%}
            <th>
                Pieces Per Bundle
            </th>
            {% endif %}
            {% if not only_rm%}
             <th>
                Batch No
            </th>
            {% endif %}
        </tr>
        {% for item in dn_items %}
        <tr>
            <td>
                {{ item.item_name }}
            </td>


            <td>
                {% if item.item_group == "Raw Material" %}
                    {{ item.qty }}
                {% else %}
                    {{item.ts_qty}}
                {% endif %}
            </td>
            {% if doc.value_pieces==1 and not only_rm %}
            <td>
                {{item.pieces}}
            </td>
            {% endif %}
             {% if not only_rm%}
            <td>
                 {% if item.item_group in ['Pavers', 'Compound Walls'] %}
                    {{ "%.0f"%uom_conversion(item.item_code, 'Bdl', 1, 'Nos') }}
                {% endif %}
            </td>
             {% endif %}
              {% if not only_rm%}
            <td colspan=3>
                 {% if item.batch_no %}
                    {{item.batch_no}}
                {% endif %}
            </td>
            {% endif %}
        </tr>
        {% endfor %}

    </table>
    <br>
    <br>
    {% set a=bundle_sum(doc.name)%}
    {% if a and not only_rm  %}
    <p style="line-height:1.5">
       <span class="bold"> Group Total</span>
    </p>
    <table style="width:100%;">


        <tr>
            <th style="width:40%;">
                Item
            </th>
            {% if doc.value_bundle==1%}
                <th>
                    Bundle
                </th>
            {% endif %}
            {% if doc.value_pieces==1%}
                <th>
                    Pieces
                </th>
            {% endif %}
            </tr>
            {% for i in a %}
                <tr>
                <td>
                    {{i['item_name']}}
                </td>
                 {% if doc.value_bundle==1%}
                    <td>
                        {{ i['Bdl'] }}
                    </td>
                {% endif %}
                {% if doc.value_pieces==1%}
                    <td>
                        {{ i['pieces'] }}
                    </td>
                {% endif %}
                </tr>
            {% endfor %}
            </table>
            {% endif %}

    <br>
    <!--<br>-->
    <!--   <sapn class="bold"> Remarks :</sapn>  <br><br>-->
    <!--   <ul>-->
    <!--       {% for data in doc.items %}-->
    <!--       <li>{{data.batch_no}}</li>-->
    <!--        {% endfor %}-->
    <!--       </ul>-->
    <!--<br>-->

</div>
 {% endif %}
