css:
main{
    font-size:13px;
     font-family: Arial, Helvetica, sans-serif;
}
.head{
    text-align:center;
}
.bold{
    font-weight:bold;
}
td,th{
    border:1px solid black;
}

html:
{% set dn_items = doc.items %}

{% if "doc.docstatus==1" %}
<div class="main">
    <div  class="head bold" style="font-size: 17px;">Sri Ganapathy Fly Ash Bricks</div><div class="head" style="font-size: 16px;">Palladam</div><div class="head bold" style="font-size: 17px;">------------------------------<br>DELIVERY SLIP<br>{{ {0:"DRAFT",1:"SUBMITTED",2:"CANCELLED"}.get(doc.docstatus) }}</div>
    <p style="line-height: 1.6;">
        <sapn class="bold"> Req No:</sapn> {{doc.name or " "}} <br>
        <sapn class="bold"> Date:</sapn> {{doc.get_formatted("posting_date")}} {{doc.get_formatted("posting_time")}}  <br>
        <sapn class="bold"> Customer:</sapn> {{doc.site_work or doc.customer }}  <br>
        {% if not doc.is_direct_delivery %}
            <sapn class="bold"> Vehicle No:</sapn> {{ doc.vehicle_no or doc.own_vehicle_no or " "}} <br>
            <sapn class="bold"> Total KM:</sapn> {{doc.distance or " "}}  <br>
            <sapn class="bold"> Driver Name:</sapn> {{doc.driver_name_1 or doc.driver_name_2 or " "}}  <br>
            <sapn class="bold"> Operator:</sapn> {% if doc.transporter=="Own Transporter" %}{% if doc.operator_ %}{{doc.operator_}}{% endif %} {% else %}{% if doc.operator %}{{frappe.db.get_value("Employee", doc.operator, "employee_name")}}{% endif %}{%endif%} <br>
        {% else %}
            <sapn class="bold"> Supplier:</sapn> {{doc.supplier or " "}}  <br>
            <sapn class="bold"> Supplier DC No:</sapn> {{doc.supplier_dc_no or " "}}  <br>
        {% endif %}
        <sapn class="bold"> Supervisor:</sapn> {{ doc.supervisor_name or " " }} <br>
        <sapn class="bold"> Phone:</sapn> {{doc.supervisor_phone_no or ""}} <br><br>
    </p>

    {% set only_rm = check_only_rm(doc.name) %}
    <table style="width:100%;">
        <tr>
            <th style="width:40%;">
                Item
            </th>
            <th>
                {% if not only_rm%}
                Bundle
                {% else %}
                Unit
                {% endif %}
            </th>
            {% if doc.value_pieces==1 and not only_rm %}
            <th>
                Pieces
            </th>
            {% endif %}
            {% if not only_rm%}
            <th>
                Pieces Per Bundle
            </th>
            {% endif %}
             {% if not only_rm%}
             <th>
                Batch No
            </th>
            {% endif %}
        </tr>
        {% for item in dn_items %}
        <tr>
            <td>
                {{ item.item_name }}
            </td>


            <td>
                {% if item.item_group == "Raw Material" %}
                    {{ item.qty }} {{ item.uom }}
                {% else %}
                    {{item.ts_qty}}
                {% endif %}
            </td>
            {% if doc.value_pieces==1 and not only_rm %}
            <td>
                {{item.pieces}}
            </td>
            {% endif %}
             {% if not only_rm%}
            <td>
                 {% if item.item_group in ['Pavers', 'Compound Walls'] %}
                    {{ "%.0f"%uom_conversion(item.item_code, 'Bdl', 1, 'Nos') }}
                {% endif %}
            </td>
             {% endif %}
             {% if not only_rm%}
            <td>
                {% if item.batch_no %}
                    {{item.batch_no}}
                {% endif %}
            </td>
             {% endif %}
        </tr>
        {% endfor %}

    </table>

    <br>
    <br>
    <br>
    <table style="width:100%;">
        <tr>
            <th style="width:30%;">
                Item
            </th>
            <th>
               Order Qty
            </th>
            <th>
               Deliver Qty
            </th>
             <th>
               Balance Qty
            </th>
             <th>
               Stock Qty
            </th>
            <th>
                Batch No
            </th>
        </tr>

        {% for item in dn_items %}
        <tr>
            <td>
              {{ item.item_name }}  
            </td>
            <td>
              {{ item.ts_required_area_qty }}  
            </td>
            <td>
               {{ item.pending_qty }}  
            </td>
            <td>
                {{ item.delivery_qty_till_date}}
            </td>
            <td>
                {{ item.actual_qty }}
            </td>
            <td>
                {{item.batch_no or ""}}
            </td>

        </tr>
        {% endfor %}
    </table>
    <br>

</div>
 {% endif %}
